import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import './Loginsignup.css';

const Loginsignup = () => {
  const history = useHistory();
  const [action, setAction] = useState("login");

  const handleLogin = () => {
    // Perform login logic here
    // Redirect to the desired page after login
    history.push('/dashboard'); // Replace '/dashboard' with the desired login page URL
  };

  const handleSignup = () => {
    // Perform signup logic here
    // Redirect to the desired page after signup
    history.push('/welcome'); // Replace '/welcome' with the desired signup page URL
  };

  return (
    <div className='login-container'>
      <div className="header">
        <div className="text"> {action}</div>
        <div className="underline"> </div>
        <div className="inputs">
          {action === "login" ? (
            <div></div>
          ) : (
            <div className="input">
              <input type="text" placeholder='name' />
            </div>
          )}

          {action === "signup" ? (
            <div></div>
          ) : (
            <div className="input">
              <input type="text" placeholder='name' />
            </div>
          )}

          <div className="input">
            <input type="password" placeholder='password'/>
          </div>
        </div>

        <div className="submit-container">
          <div className={action === "login" ? "submit gray" : "submit"} onClick={handleSignup}>Signup</div>
          <div className={action === "signup" ? "submit gray" : "submit"} onClick={handleLogin}>Login</div>
          <br/>
        </div>
      </div>
    </div>
  )
}

export default Loginsignup;
