import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import './Loginsignup.css';

const Loginsignup = () => {
  const history = useHistory();
  const [action, setAction] = useState("login");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = () => {
    // Perform login logic here
    // You can check username and password against your authentication system
    if (username === "admin" && password === "password") {
      // Redirect to the desired page after successful login
      history.push('/dashboard');
    } else {
      // Handle login failure
      alert("Invalid username or password");
    }
  };

  const handleSignup = () => {
    // Perform signup logic here
    // You can create a new user account with the provided username and password
    // Redirect to the desired page after successful signup
    history.push('/welcome');
  };

  return (
    <div className='login-container'>
      <div className="header">
        <div className="text"> {action}</div>
        <div className="underline"> </div>
        <div className="inputs">
          {action === "login" ? (
            <div></div>
          ) : (
            <div className="input">
              <input
                type="text"
                placeholder='name'
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
          )}

          {action === "signup" ? (
            <div></div>
          ) : (
            <div className="input">
              <input
                type="text"
                placeholder='name'
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
          )}

          <div className="input">
            <input
              type="password"
              placeholder='password'
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
        </div>

        <div className="submit-container">
          <div className={action === "login" ? "submit gray" : "submit"} onClick={handleSignup}>
            Signup
          </div>
          <div className={action === "signup" ? "submit gray" : "submit"} onClick={handleLogin}>
            Login
          </div>
          <br />
        </div>
      </div>
    </div>
  );
};

export default Loginsignup;
